function initMap() {

  var center = new google.maps.LatLng(37.7877034,-122.4319239);

  // Intialize our map
  var mapOptions = {
    zoom: 13,
    center: center,
    styles: styles,
    mapTypeControl: true,
    mapTypeControlOptions: {
      style: google.maps.MapTypeControlStyle.HORIZONTAL_BAR,
      position: google.maps.ControlPosition.TOP_RIGHT
    },
  };

  map = new google.maps.Map(document.getElementById("map"), mapOptions);
  google.maps.event.addDomListener(window, 'load', mapLoaded);

}

// handles any errors in loading the map
function googleError() {
    console.error('Failed to load Google Map');
    alert('Failed to load Google Map');
}

// called when google maps has loaded
function mapLoaded(){
    console.log('map loaded!');
    viewModel = new ViewModel();
    ko.applyBindings(viewModel);
}

var Location = function(data) {
    var self = this;

    this.actor_1 = data.actor_1;
    this.actor_2 = data.actor_2;
    this.actor_3 = data.actor_3;
    this.director = data.director;
    this.distributor = data.distributor;
    this.fun_facts = data.fun_facts;
    this.locations = data.locations;
    this.place_id = data.place_id;
    this.production_company = data.production_company;
    this.release_year = data.release_year;
    this.title = data.title;
    this.writer = data.writer;

    this.lat = ko.observable(data.geometry.location.lat);
    this.lng = ko.observable(data.geometry.location.lng);
    this.address = ko.observable(data.formatted_address);

    var image = 'img/movie.png';

    var coords = new google.maps.LatLng(this.lat(), this.lng());
    var marker = new google.maps.Marker({
        position: coords,
        map: map,
        icon: image,
        title: self.title // shows up as a tooltip
    });

    // create a ko observable for the map marker
    this.marker = ko.observable(marker);

    google.maps.event.addListener(marker, 'click', function(){
        viewModel.markerClicked(self);
    });

    // hides the map marker by setting it's map paramter to null
    this.hideMarker = function() {
        self.marker().setMap(null);
    };

    // shows the map marker
    this.showMarker = function() {
        self.marker().setMap(map);
    };

    this.toggleMarkerBounce = function() {
        if (marker.getAnimation() === undefined || marker.getAnimation() === null) {
            self.marker().setAnimation(google.maps.Animation.BOUNCE);
        } else {
            self.marker().setAnimation(null);
        }
    };

    // creates and displays the info window for a map location
    this.createInfoContent = function() {
        // load data from the The Movie Database API
        self.loadData().then(function(response){
            console.log('Success!', response);
            var data = JSON.parse(response);
            console.log(data);

            // build the content string from the data
            var poster_url = '/img/no-poster-available.jpg';  // placeholder poster art image
            var release_date = (self.release_year === undefined) ? '' : self.release_year;
            var overview = '';

            const results = data.results[0];
            if (results !== undefined) {
                const poster = results.poster_path;
                console.log(poster);
                poster_url = 'https://image.tmdb.org/t/p/original/' + poster;
                console.log(poster_url);

                release_date = results.release_date;
                overview = results.overview;
            } else {
                console.log('no results for selected item.');
            }

            // build up the actors string
            var actors;
            if (self.actor_1 !== undefined) {
                actors = self.actor_1;
            }
            if (self.actor_2 !== undefined) {
                actors += (', ' + self.actor_2);
            }
            if (self.actor_3 !== undefined) {
                actors += (', ' + self.actor_3);
            }

             // build the content string from the data
            content =
                '<div class="panel panel-info" id="content">' +
                    '<div class="panel-heading">' +
                        '<h3 class="panel-title">' +  self.title + '</h3>' +
                    '</div>' +
                    '<div class="panel-body">' +
                        '<div class="media">' +
                            '<div class="media-left">' +
                                '<img class="media-object poster_image" src="' + poster_url + '" alt="movie poster art">' +
                            '</div>' +
                            '<div class="media-body">' +
                                '<p> <strong>Location: </strong>' + self.locations + '</p>' +
                                '<p> <strong>Address: </strong>' + self.address() + '</p>' +
                                ((self.fun_facts === undefined) ? '<p></p>' : '<p> <strong>Fun Facts: </strong>' + self.fun_facts) + '</p>' +
                                '<p> <strong>Released: </strong>' + release_date + '</p>' +
                                '<p> <strong>Distributor: </strong>' + self.distributor + '</p>' +
                                '<p> <strong>Production Company: </strong>' + self.production_company + '</p>' +
                                ((self.director === undefined) ? '' : '<p> <strong>Directed by: </strong>' + self.director + '</p>') +
                                '<p> <strong>Starring:</strong> ' + actors + '</p>' +
                                '<p> <strong>Overview: </strong>' + overview + '</p>' +
                            '</div' +
                        '</div>' +

                        '<div id="siteNotice"></div>' +
                    '</div>' +
                '</div>';

                // set the content on the info window
                infowindow = new google.maps.InfoWindow({
                    content: content
                });
                infowindow.open(map, marker);
                viewModel.setInfoWindow(infowindow);

        }, function(Error) {
            console.log(Error);
        });
    };

    this.loadData = function() {
        var url = 'https://api.themoviedb.org/3/search/movie?api_key=9c8b8a24a248fed2e25eb1f8d2f29d13&language=en-US&query=' +
                    this.title + '&page=1&include_adult=false&year=' +
                    this.release_year;

        return new Promise(function(resolve, reject) {
            var request = new XMLHttpRequest();
            request.open('GET', url);
            request.onload = function() {
                if (request.status === 200) {
                    // resolve the promise by passing back the response
                    resolve(request.response);
                } else {
                    // upon failure, reject the promise with an error message
                    reject(Error('Failed to load successfully; error code:' + request.statusText));
                }
            };

            // handle the case where the entire request fails.
            request.onerror = function() {
                reject(Error('Network error.'));
            };

            // send the request
            request.send();
        });
    };
};

var ViewModel = function() {
    // store the outer 'this' parameter which represents the ViewModel
    var self = this;

    this.locations = ko.observableArray([]);

    this.filteredList = ko.observableArray([]);

    this.filmSearch = ko.observable('');

    // Create a new Location object for each item in the locations array.
    locations.forEach(function(loc){
        self.locations.push( new Location(loc) );
    });

    // set the current location
    this.currentLocation = ko.observable( this.locations()[0] );

    // this makes sure only one infowindow is open at a time
    this.setInfoWindow = function(infowindow){
        if (self.infowindow !== undefined) {
            self.infowindow().close();
        }
        self.infowindow = ko.observable(infowindow);
    };

    // handles a click on a list item
    this.locationClicked = function(location) {

        // console.log(this);

        // clear any marker that is currently bouncing.
        self.currentLocation().marker().setAnimation(null);

        // update the current location
        self.currentLocation(this);

        // toggle marker bounce
        location.toggleMarkerBounce();

        // center the map on the marker location
        location.marker().map.setCenter(location.marker().position);

        // load the content and display info
        location.createInfoContent();
    };

    // handles a click on a map marker
    this.markerClicked = function(location) {

        // clear any marker that is currently bouncing.
        self.currentLocation().marker().setAnimation(null);

        // update the current location
        self.currentLocation(location);

        // load the content and display info
        location.createInfoContent();

        // toggle marker bounce
        location.toggleMarkerBounce();
    };

    // uses a regular expression to match by location title
    this.findMatches = function(wordToMatch) {
        return  this.locations().filter(location => {
            const regex = new RegExp(wordToMatch, 'gi');  // global, insensitive
            return location.title.match(regex);
        });
    };

    // filter the location list by film title.
    this.filteredResult = ko.computed(function(){

        // hide all map markers
        self.filteredList().forEach(location => {
            location.hideMarker();
        });

        // remove all items from our filtered list
        self.filteredList.removeAll();

        // get the search term from the input box
        var searchTerm = self.filmSearch().toLowerCase();

        // get matching locations
        results = self.findMatches(searchTerm);
        // console.log(results);

        // the resulting list will not be a ko list, so we need to add all of the results to our filtered list.
        results.forEach(result => {
            // show the map marker for this result
            result.showMarker();
            // add the result to our filtered list.
            self.filteredList.push(result);
        });

    }, this);
};


